'use strict'

const morgan = require('morgan')

/**
 * @param  {String}   options.format  Log format.
 * @param  {Function} options.skip    Callback which resolves if the log should
 *                                    be printed.
 * @return {Function}                 Middleware.
 */
const factory = ({ format, skip }) => {
  return morgan(format, { skip })
}

module.exports = { factory }
